import React from 'react';
import { Link } from 'react-router-dom';
import { useFavourites } from '../components/Favourites';
import { useCart } from '../components/CartContext'; // Импортируем хук useCart
import '../styles/Favourites.css';

const Favourites = () => {
  const { favourites, removeFromFavourites } = useFavourites();
  const { addToCart } = useCart(); // Получаем функцию addToCart из контекста корзины

  return (
    <div className="favourites-container">
      <h2>Избранное</h2>
      {favourites.length > 0 ? (
        favourites.map((fav) => (
          <div className="favourite-item" key={fav.id}>
            <Link to={`/product/${fav.id}`}>
              <img src={fav.image} alt={fav.name} className="favourite-item-image" />
            </Link>
            <div className="favourite-item-details">
              <h3>{fav.name}</h3>
              <p>{fav.price}</p>
              <div className="button-group">
                <button className="cart-button" onClick={() => addToCart(fav)}>
                  Добавить в корзину
                </button>
                <button className="remove-button" onClick={() => removeFromFavourites(fav.id)}>
                  Удалить из избранного
                </button>
              </div>
            </div>
          </div>
        ))
      ) : (
        <p className="empty-message">Избранных товаров нет</p>
      )}
    </div>
  );
};

export default Favourites;
