import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/Catalog.css';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Импортируем 

const Catalog = () => {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [categoryFilter, setCategoryFilter] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3001/products/')
      .then(res => setData(res.data))
      .catch(err => console.log(err));
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleCategoryFilter = (event) => {
    setCategoryFilter(event.target.value);
  };

  const filteredData = data
    .filter(item => 
      item.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (categoryFilter ? item.category === categoryFilter : true)
    );

  const uniqueCategories = [...new Set(data.map(item => item.category))];

  return (
    <div className="container2">
      <div className="info">
        <p>
          <b>Все товары для покупки:</b>
        </p>
        <div className="shopcategory-search">
          <input 
            type="text" 
            placeholder="Поиск" 
            value={searchTerm} 
            onChange={handleSearch} 
          />
        </div>
        <div className="shopcategory-sort">
          <select onChange={handleCategoryFilter}>
            <option value="">Все категории</option>
            {uniqueCategories.map((category, index) => (
              <option key={index} value={category}>{category}</option>
            ))}
          </select>
        </div>
      </div>
      <div className="items">
        {filteredData.map((d, i) => (
          <div className="item" key={i}>
            <Link to={`/product/${d.id}`}><img src={d.image} alt={d.name} className="item-image" /></Link>
            <div className="item-details">
              <p className="item-name">{d.name}</p>
              <pre className="item-price">{d.price}</pre>
              <p className="item-category">{d.category}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
