import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Импортируем motion из Framer Motion
import { useFavourites } from '../components/Favourites';
import '../styles/Favourites.css';

const Favourites = () => {
  const { favourites, removeFromFavourites } = useFavourites();
  const [showNotification, setShowNotification] = useState(false); // Состояние для отображения уведомления
  const [notificationMessage, setNotificationMessage] = useState(''); // Сообщение уведомления

  // Функция для показа уведомления
  const showNotificationMessage = (message) => {
    setNotificationMessage(message);
    setShowNotification(true);
    setTimeout(() => {
      setShowNotification(false);
      setNotificationMessage('');
    }, 2000); // Скрыть уведомление через 2 секунды
  };

  return (
    <div className="favourites-container">
      <h2>Избранное</h2>
      {favourites.length > 0 ? (
        favourites.map((fav) => (
          <div className="favourite-item" key={fav.id}>
            <Link to={`/product/${fav.id}`}>
              <img src={fav.image} alt={fav.name} className="favourite-item-image" />
            </Link>
            <div className="favourite-item-details">
              <h3>{fav.name}</h3>
              <p>{fav.price}</p>
              <button onClick={() => removeFromFavourites(fav.id)}>Удалить из избранного</button>
              <motion.button className="button blue"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => {
                  // Добавление товара в корзину
                  // Вам нужно заменить этот кусок кода на вашу логику добавления в корзину
                  // Например:
                  // addToCart(fav);
                  showNotificationMessage('Товар добавлен в корзину!');
                }}
              >
                Добавить в корзину
              </motion.button>
            </div>
          </div>
        ))
      ) : (
        <p>Избранных товаров нет</p>
      )}
      {/* Анимированное уведомление */}
      {showNotification && (
        <motion.div className="notification"
          initial={{ opacity: 0, y: 100 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 100 }}
          transition={{ duration: 0.5 }}
        >
          {notificationMessage}
        </motion.div>
      )}
    </div>
  );
};

export default Favourites;
