import React, { useState } from 'react';
import '../styles/Navbar.css';
import logo from '../components/Assets/logo.png';
import { Link, NavLink } from 'react-router-dom';

export default function Navbar() {
  const [menu, setMenu] = useState('main');

  return (
    <div className="navbar">
      <div className="nav-logo">
        <img src={logo} alt="logo" />
        <p>Наценочка</p>
      </div>
      <ul className="nav-menu">
        <li onClick={() => { setMenu('main') }}>
          <NavLink to='/' isActive={() => menu === 'main'} activeClassName="active">
            Главная
          </NavLink>
          {menu === "main" && <hr />}
        </li>
        <li onClick={() => { setMenu('catalog') }}>
          <NavLink to='/catalog' isActive={() => menu === 'catalog'} activeClassName="active">
            Каталог
          </NavLink>
          {menu === "catalog" && <hr />}
        </li>
        <li onClick={() => { setMenu('favourites') }}>
          <NavLink to='/favourites' isActive={() => menu === 'favourites'} activeClassName="active">
            Избранное
          </NavLink>
          {menu === "favourites" && <hr />}
        </li>
        <li onClick={() => { setMenu('cart') }}>
          <NavLink to='/cart' isActive={() => menu === 'cart'} activeClassName="active">
            Корзина
          </NavLink>
          {menu === "cart" && <hr />}
        </li>
        <li onClick={() => { setMenu('order') }}>
          <NavLink to='/order' isActive={() => menu === 'order'} activeClassName="active">
            Заказ
          </NavLink>
          {menu === "order" && <hr />}
        </li>
      </ul>
    </div>
  );
}
