import React, { useEffect} from 'react';
import { useCart } from '../components/CartContext';
import '../styles/Cart.css';

const Cart = () => {
  const { cart, removeFromCart } = useCart();
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const calculatedTotal = cart.reduce((acc, product) => acc + parseFloat(product.price), 0);
    setTotal(calculatedTotal.toFixed(2));
  }, [cart]);

  return (
    <div className="cart-container">
      <h2>Корзина</h2>
      {cart.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        cart.map((product) => (
          <div key={product.cartId} className="cart-item">
            <img src={product.image} alt={product.name} className="cart-item-image" />
            <div className="cart-item-details">
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <button onClick={() => removeFromCart(product.cartId)}>Удалить из корзины</button>
            </div>
          </div>
        ))
      )}
      <div className="cart-total">
        Общая стоимость: {total} руб.
      </div>
    </div>
  );
};

export default Cart;
