import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/Catalog.css';
import dropdown_icon from '../components/Assets/dropdown_icon.png';
import { Link } from 'react-router-dom';

const Catalog = () => {
  const [data, setData] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3001/products/')
      .then(res => {
        setData(res.data);
        const uniqueCategories = [...new Set(res.data.map(product => product.category))];
        setCategories(uniqueCategories);
      })
      .catch(err => console.log(err));
  }, []);

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleCategoryChange = (event) => {
    setSelectedCategory(event.target.value);
  };

  const filteredData = data.filter((product) => {
    return (
      (selectedCategory === '' || product.category === selectedCategory) &&
      product.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  return (
    <div className="container">
      <div className="info">
        <p>
          <b>Все товары для покупки:</b>
        </p>
        <div className="shopcategory-search">
          <input
            type="text"
            placeholder="Поиск товаров..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
        <div className="shopcategory-sort">
          <select onChange={handleCategoryChange} value={selectedCategory}>
            <option value="">Все категории</option>
            {categories.map((category, index) => (
              <option key={index} value={category}>{category}</option>
            ))}
          </select>
          <img src={dropdown_icon} alt='dropdown_icon'/>
        </div>
      </div>
      <div className="items">
        {filteredData.map((d, i) => (
          <div className="item" key={i}>
            <Link to={`/product/${d.id}`}><img src={d.image} alt={d.name} className="item-image" /></Link>
            <div className="item-details">
              <p className="item-name">{d.name}</p>
              <pre className="item-price">{d.price}</pre>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Catalog;
