// src/components/OrderForm.js
import React from 'react';
import { useForm } from 'react-hook-form';
import emailjs from '@emailjs/browser';
import '../styles/order-form.css';

const OrderForm = () => {
  const { register, handleSubmit, reset } = useForm();

  const onSubmit = (data) => {
    emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', data, 'YOUR_PUBLIC_KEY')
      .then((result) => {
        console.log('Заказ подтвержден!', result.text);
        reset();
      }, (error) => {
        console.log('Ошибка при подтверждении заказа:', error.text);
      });
  };

  return (
    <form className="order-form" onSubmit={handleSubmit(onSubmit)}>
      <h3>Форма заказа</h3>
      <input {...register('name')} placeholder="ФИО" required />
      <input {...register('address')} placeholder="Адрес" required />
      <select {...register('paymentMethod')} required>
        <option value="cash">Наличные</option>
        <option value="card">Карта</option>
      </select>
      <button type="submit">Оформить заказ</button>
    </form>
  );
};

export default OrderForm;
