import axios from 'axios';
import React, { useEffect, useState } from 'react';
import '../styles/Catalog.css';
import dropdown_icon from '../components/Assets/dropdown_icon.png'
import { Link } from 'react-router-dom';

const Shop = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:3001/products')
      .then(res => setData(res.data))
      .catch(err => console.log(err));
  }, []);

  return (
    <div className="container">
      <div className="info">
        <p>
          <b>Все товары для покупки:</b>
        </p>
        <div className="shopcategory-serch">
          поиск
        </div>
        <div className="shopcategory-sort">
          Сортировать по <img src={dropdown_icon} alt='dropdown_icon'/>
        </div>
      </div>
      <div className="items">
        {data.map((d, i) => (
          <div className="item" key={i}>
            <Link to={`/product/${d.id}`}><img src={d.image} alt={d.name} className="item-image" /></Link>
            <div className="item-details">
              <p className="item-name">{d.name}</p>
              <pre className="item-price">{d.price}</pre>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Shop;
