import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../components/CartContext';
import { useFavourites } from '../components/Favourites';
import '../styles/Product.css';

const Product = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const { addToCart } = useCart();
  const { favourites, addToFavourites, removeFromFavourites } = useFavourites();

  useEffect(() => {
    axios.get(`http://localhost:3001/products/${productId}`)
      .then(res => setProduct(res.data))
      .catch(err => console.log(err));
  }, [productId]);

  if (!product) {
    return <div>Loading...</div>;
  }

  const isFavourite = favourites && favourites.some((fav) => fav.id === product.id);

  return (
    <div className="product-container">
      <img src={product.image} alt={product.name} className="product-image" />
      <div className="product-details">
        <h2>{product.name}</h2>
        <p>{product.price}</p>
        <button onClick={() => addToCart(product)}>Добавить в корзину</button>
        {isFavourite ? (
          <button onClick={() => removeFromFavourites(product.id)}>Удалить из избранного</button>
        ) : (
          <button onClick={() => addToFavourites(product)}>Добавить в избранное</button>
        )}
      </div>
    </div>
  );
};

export default Product;
