import React, { useState } from 'react';
import './OrderPage.css';

function OrderPage() {
  const [form, setForm] = useState({ name: '', address: '', payment: '' });
  const [captcha, setCaptcha] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prevForm) => ({ ...prevForm, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (captcha) {
      // Отправка заказа
      console.log('Заказ отправлен:', form);
      alert('Заказ успешно оформлен!');
    } else {
      setError('Пожалуйста, подтвердите, что вы не робот.');
    }
  };

  return (
    <div className="order-page">
      <h2>Оформление заказа</h2>
      <form className="order-form" onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">ФИО</label>
          <input type="text" id="name" name="name" value={form.name} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="address">Адрес</label>
          <input type="text" id="address" name="address" value={form.address} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="payment">Способ оплаты</label>
          <select id="payment" name="payment" value={form.payment} onChange={handleChange} required>
            <option value="">Выберите способ оплаты</option>
            <option value="card">Карта</option>
            <option value="cash">Наличные</option>
          </select>
        </div>
        <div className="form-group">
          <label htmlFor="captcha">Подтвердите, что вы не робот</label>
          <input type="checkbox" id="captcha" name="captcha" checked={captcha} onChange={() => setCaptcha(!captcha)} />
        </div>
        {error && <p className="error">{error}</p>}
        <button type="submit">Оформить заказ</button>
      </form>
    </div>
  );
}

export default OrderPage;
