import React, { useEffect, useState } from 'react';
import { useCart } from '../components/CartContext';
import { Link } from 'react-router-dom';
import { CSSTransition, TransitionGroup } from 'react-transition-group'; // Импортируем CSSTransition и TransitionGroup
import '../styles/Cart.css';

const Cart = () => {
  const { cart, removeFromCart } = useCart();
  const [total, setTotal] = useState(0);

  useEffect(() => {
    const calculatedTotal = cart.reduce((acc, product) => acc + parseFloat(product.price), 0);
    setTotal(calculatedTotal.toFixed(2));
  }, [cart]);

  const handleRemove = (cartId) => {
    removeFromCart(cartId);
  };

  return (
    <div className="cart-container">
      <h2>Корзина</h2>
      {cart.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <TransitionGroup className="cart-items">
          {cart.map((product) => (
            <CSSTransition key={product.cartId} timeout={500} classNames="item">
              <div className="cart-item">
                <img src={product.image} alt={product.name} className="cart-item-image" />
                <div className="cart-item-details">
                  <h3>{product.name}</h3>
                  <p>{product.price}</p>
                  <button onClick={() => handleRemove(product.cartId)}>Удалить из корзины</button>
                </div>
              </div>
            </CSSTransition>
          ))}
        </TransitionGroup>
      )}
      <div className="cart-total">
        Общая стоимость: {total} руб.
      </div>
      <Link to="/order" className="checkout-button">Оформить заказ</Link>
    </div>
  );
};

export default Cart;
