import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { motion } from 'framer-motion'; // Импортируем motion из Framer Motion
import { useCart } from '../components/CartContext';
import { useFavourites } from '../components/Favourites';
import '../styles/Product.css';

const Product = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [showNotification, setShowNotification] = useState(false); // Состояние для отображения уведомления
  const [notificationMessage, setNotificationMessage] = useState(''); // Сообщение уведомления
  const { addToCart } = useCart();
  const { favourites, addToFavourites, removeFromFavourites } = useFavourites();

  useEffect(() => {
    axios.get(`http://localhost:3001/products/${productId}`)
      .then(res => setProduct(res.data))
      .catch(err => console.log(err));
  }, [productId]);

  if (!product) {
    return <div>Загрузка...</div>;
  }

  const isFavourite = Array.isArray(favourites) && favourites.some((fav) => fav.id === product.id);

  // Функция для показа уведомления
  const showNotificationMessage = (message) => {
    setNotificationMessage(message);
    setShowNotification(true);
    setTimeout(() => {
      setShowNotification(false);
      setNotificationMessage('');
    }, 2000); // Скрыть уведомление через 2 секунды
  };

  // Добавление в корзину
  const handleAddToCart = () => {
    addToCart(product);
    showNotificationMessage('Товар добавлен в корзину!');
  };

  // Добавление/удаление из избранного
  const handleToggleFavourite = () => {
    if (isFavourite) {
      removeFromFavourites(product.id);
      showNotificationMessage('Товар удален из избранного!');
    } else {
      addToFavourites(product);
      showNotificationMessage('Товар добавлен в избранное!');
    }
  };

  return (
    <div className="product-container">
      <motion.div className="product-image-container"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        <img src={product.image} alt={product.name} className="product-image" />
      </motion.div>
      <div className="product-details">
        <h2>{product.name}</h2>
        <p className="product-price">{product.price}</p>
        <p>{product.description}</p>
        <div className="product-buttons">
          <motion.button className="button blue"
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleAddToCart}
          >
            Добавить в корзину
          </motion.button>
          {isFavourite ? (
            <motion.button className="button blue"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleToggleFavourite}
            >
              Удалить из избранного
            </motion.button>
          ) : (
            <motion.button className="button blue"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleToggleFavourite}
            >
              Добавить в избранное
            </motion.button>
          )}
        </div>
      </div>
      {/* Анимированное уведомление */}
      {showNotification && (
        <motion.div className="notification"
          initial={{ opacity: 0, y: 100 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: 100 }}
          transition={{ duration: 0.5 }}
        >
          {notificationMessage}
        </motion.div>
      )}
    </div>
  );
};

export default Product;
