// src/components/ContactForm.js
import React from 'react';
import { useForm } from 'react-hook-form';
import emailjs from '@emailjs/browser';
import '../styles/contact-form.css';

const ContactForm = () => {
  const { register, handleSubmit, reset } = useForm();

  const onSubmit = (data) => {
    emailjs.send('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', data, 'YOUR_PUBLIC_KEY')
      .then((result) => {
        console.log('Сообщение отправлено!', result.text);
        reset();
      }, (error) => {
        console.log('Ошибка при отправке сообщения:', error.text);
      });
  };

  return (
    <form className="contact-form" onSubmit={handleSubmit(onSubmit)}>
      <h3>Форма обратной связи</h3>
      <input {...register('name')} placeholder="Ваше имя" required />
      <input {...register('email')} type="email" placeholder="Ваш email" required />
      <textarea {...register('message')} placeholder="Ваше сообщение" required></textarea>
      <button type="submit">Отправить</button>
    </form>
  );
};

export default ContactForm;
