import React, { useState } from 'react';
import { useForm } from 'react-hook-form'; // Импортируем необходимый хук
import ReCAPTCHA from 'react-google-recaptcha';
import emailjs from '@emailjs-com';
import '../styles/Order.css';

const Order = () => {
  const { register, handleSubmit, formState: { errors } } = useForm(); // Используем useForm для управления формой
  const [recaptchaVerified, setRecaptchaVerified] = useState(false);
  const [message, setMessage] = useState('');

  const onSubmit = (data) => {
    if (recaptchaVerified) {
      emailjs.send('your_service_id', 'your_template_id', data, 'your_user_id')
        .then(() => {
          setMessage('Ваш заказ успешно оформлен и отправлен на почту.');
        })
        .catch(() => {
          setMessage('Произошла ошибка при оформлении заказа. Попробуйте снова.');
        });
    } else {
      setMessage('Подтвердите, что вы не робот.');
    }
  };

  const handleRecaptcha = (value) => {
    setRecaptchaVerified(!!value);
  };

  return (
    <div className="checkout-container">
      <h2>Оформление заказа</h2>
      <form onSubmit={handleSubmit(onSubmit)}>
        <div className="form-group">
          <label>Адрес почты</label>
          <input
            type="text"
            {...register('address', { required: true })}
          />
          {errors.address && <span className="error">Поле обязательно для заполнения</span>}
        </div>
        <div className="form-group">
          <label>ФИО</label>
          <input
            type="text"
            {...register('name', { required: true })}
          />
          {errors.name && <span className="error">Поле обязательно для заполнения</span>}
        </div>
        <div className="form-group">
          <label>Способ оплаты</label>
          <select
            {...register('paymentMethod', { required: true })}
          >
            <option value="">Выберите способ оплаты</option>
            <option value="card">Карта</option>
            <option value="cash">Наличные</option>
          </select>
          {errors.paymentMethod && <span className="error">Выберите способ оплаты</span>}
        </div>
        <div className="form-group">
          <ReCAPTCHA
            sitekey="your-recaptcha-site-key"
            onChange={handleRecaptcha}
          />
          {errors.recaptcha && <span className="error">Подтвердите, что вы не робот</span>}
        </div>
        <button type="submit">Подтвердить заказ</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Order;
